(function(){function cleanup(){const existingStyle=document.querySelector("#unfold-styles");if(existingStyle)existingStyle.remove()}cleanup();const fragment=document.createDocumentFragment();const messages={ui:{openAll:"Open All",closeAll:"Close All"}};const style=document.createElement("style");style.id="unfold-styles";style.textContent=`\n    .unfold-toggle {\n      padding: 5px 10px;\n      border: none;\n      border-radius: 3px;\n      background: #0d6efd;\n      color: #fff;\n      font-size: 1em;\n      position: fixed;\n      top: 1rem;\n      right: 1rem;\n      z-index: 9999;\n    }\n    .unfold-toggle:hover {\n      background: #1e1e1e !important;\n    }\n    @keyframes fadeIn {\n      from {\n        opacity: 0;\n        transform: translateY(-8px);\n      }\n      to {\n        opacity: 1;\n        transform: translateY(0);\n      }\n    }\n    @media (prefers-reduced-motion: no-preference) {\n      .unfold-toggle {\n        animation: fadeIn 0.5s ease-out;\n      }\n    }\n    @media (prefers-color-scheme: dark) {\n      .unfold-toggle {\n        background: #1e1e1e;\n        color: #f0f0f0\n      }\n    }\n  `;fragment.appendChild(style);const toggleButton=document.createElement("button");toggleButton.textContent=`${messages.ui.openAll}`;toggleButton.className="unfold-toggle";fragment.appendChild(toggleButton);function toggleAccordions(){const accordions=document.querySelectorAll(".fr-accordion__title");const isOpen=Array.from(accordions).some(accordion=>accordion.getAttribute("aria-expanded")==="true");accordions.forEach(accordion=>{accordion.click()});const allOpen=!isOpen;toggleButton.textContent=allOpen?messages.ui.closeAll:messages.ui.openAll}toggleButton.addEventListener("click",toggleAccordions);requestAnimationFrame(()=>{document.body.insertBefore(fragment,document.body.firstChild)})})();